import matplotlib.pyplot as pltimport pandas as pdimport requestsclass fearGreed:        def __init__(self):        self.df = pd.DataFrame()        def downloadData(self, limit):        resp = requests.get('https://api.alternative.me/fng/?limit={0}'.format(limit))        if (resp.status_code == 200):            data = resp.json()['data']            df = pd.DataFrame.from_dict(data)            df['Date'] = pd.to_datetime(df['timestamp'], unit='s', yearfirst=True)            df['value'] = df['value'].astype('int32')            df['value_classification'] = df['value_classification'].astype('string')            df = df[['value', 'value_classification', 'Date']]            df.set_index('Date', inplace=True)            self.df = df                def getData(self):        return self.df        def plot_all(self, btcDf):        joinDf = self.df.join(btcDf['Close'])        joinDf.dropna(inplace=True)        fig, ax1 = plt.subplots(figsize=(16,7))                color = 'tab:orange'        ax1.set_xlabel('Date')        ax1.set_ylabel('Fear & Greed Index', color=color)        ax1.plot(joinDf.index, joinDf['value'], color=color)        ax1.tick_params(axis='y', labelcolor=color)                ax2 = ax1.twinx()        color = 'tab:grey'        ax2.set_ylabel('btc-usd', color=color)  # we already handled the x-label with ax1        ax2.plot(joinDf.index, joinDf['Close'], color=color)        ax2.tick_params(axis='y', labelcolor=color)                fig.autofmt_xdate()        fig.tight_layout()        plt.show()                    