import pandas as pdimport matplotlib.pyplot as pltdef performance(df):    d = dict()    d['7days'] = round((df['Close'].iloc[-1] - df['Close'].iloc[-7]) /  df['Close'].iloc[-7] * 100, 2)    d['21days'] = round((df['Close'].iloc[-1] - df['Close'].iloc[-21]) /  df['Close'].iloc[-21] * 100, 2)    d['180days'] = round((df['Close'].iloc[-1] - df['Close'].iloc[-180]) /  df['Close'].iloc[-180] * 100, 2)    d['365days'] = round((df['Close'].iloc[-1] - df['Close'].iloc[-365]) /  df['Close'].iloc[-365] * 100, 2)    return pd.DataFrame.from_dict(d, orient = 'index')def corr(firstAsset, secondAsset, ranges):    d = dict()    df = pd.DataFrame()    df['firstAsset'] = firstAsset['Close']    df['secondAsset'] = secondAsset['Close']    for r in ranges:        tmpDf = df[-r:-1]        d[str(r)+'days'] = [tmpDf.corr().iloc[0][1:][0]]    return pd.DataFrame.from_dict(d, orient = 'index')            